import React, { Component } from "react";


class Pad extends Component {
    constructor(props){
        super(props);
        this.state = {
            position: style={
                margin:"0 auto",
            }
        };
    }
    tabPosition = (xRight, xLeft) => {
        xRight += 5;
        xLeft += 5;
    }
    render() {

        return (
            <div
            style={{
                width:"400px",
                height:"500px",
                margin: "0 auto",
                position:"relative",
                display:"flex",
            }}
            >
                <div className="left"
                    style= {{
                        backgroundColor: "#70C3B3",
                        width:"50%",
                        height:"500px",
                    }}
                >
                </div>
                <div className="right"
                    style={{
                        backgroundColor:"#1E8873",
                        width:"50%",
                        height:"500px",
                    }}
                >
                </div>
                <div className="TabBarre"
                    style={{
                        width:"15px",
                        height:"5px",
                        backgroundColor: "black",
                        position: "absolute",
                        bottom:"50px",
                    }}
                >
                </div>
            </div>


        );
    }
}







export default Pad;
